#include <iostream>

int starts_one_ends(int n) {
    if (n <= 0) {
        throw std::invalid_argument("Input n must be a positive integer.");
    }

    long long countStartWithOne = 9 * (long long)pow(10, n - 2);
    long long countEndWithOne = 9 * (long long)pow(10, n - 2);
    long long overlapCount = (long long)pow(10, n - 2);

    long long totalCount = countStartWithOne + countEndWithOne - overlapCount;

    return totalCount;
}


