#include<stdio.h>
#include<string>
using namespace std;

string encrypt(string s){
    string result = "";
    int shift = 2 * 2; // calculate the shift value

    for (int i = 0; i < s.length(); i++) {
        char c = s[i];

        // check if the character is an alphabet letter
        if (c >= 'a' && c <= 'z') {
            // calculate the new character by shifting down by shift places
            char new_c = 'a' + (c - 'a' + shift) % 26;
            result += new_c;
        } else if (c >= 'A' && c <= 'Z') {
            // handle uppercase letters
            char new_c = 'A' + (c - 'A' + shift) % 26;
            result += new_c;
        } else {
            // if the character is not a letter, add it to the result as is
            result += c;
        }
    }

    return result;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (encrypt("hi") == "lm");
    assert (encrypt("asdfghjkl") == "ewhjklnop");
    assert (encrypt("gf") == "kj");
    assert (encrypt("et") == "ix");
    assert (encrypt("faewfawefaewg")=="jeiajeaijeiak");
    assert (encrypt("hellomyfriend")=="lippsqcjvmirh");
    assert (encrypt("dxzdlmnilfuhmilufhlihufnmlimnufhlimnufhfucufh")=="hbdhpqrmpjylqmpyjlpmlyjrqpmqryjlpmqryjljygyjl");
    assert (encrypt("a")=="e");
}

