// Custom sorting function to sort coordinates first by row in ascending order, then by column in descending order
bool compareCoordinates(const vector<int>& a, const vector<int>& b) {
    if (a[0] == b[0]) {
        return a[1] > b[1];
    }
    return a[0] < b[0];
}

vector<vector<int>> get_row(vector<vector<int>> lst, int x) {
    vector<vector<int>> coordinates;

    // Iterate through the 2D vector using nested loops
    for (int i = 0; i < lst.size(); i++) {
        for (int j = 0; j < lst[i].size(); j++) {
            // Check if the current element matches the input x
            if (lst[i][j] == x) {
                // Store the coordinates (row, column) in the coordinates vector
                coordinates.push_back({i, j});
            }
        }
    }

    // Sort the coordinates vector using the custom sorting function
    sort(coordinates.begin(), coordinates.end(), compareCoordinates);

    return coordinates;
}

