int is_bored(string S) {
    int boredomCount = 0;
    string sentence;
    for (char c : S) {
        if (c == '.' || c == '?' || c == '!') {
            if (!sentence.empty() && sentence[0] == 'I') {
                boredomCount++;
            }
            sentence.clear();
        } else {
            sentence += c;
        }
    }
    // Check the last sentence
    if (!sentence.empty() && sentence[0] == 'I') {
        boredomCount++;
    }
    return boredomCount;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (is_bored("Hello world") == 0);
    assert (is_bored("Is the sky blue?") == 0);
    assert (is_bored("I love It !") == 1);
    assert (is_bored("bIt") == 0);
    assert (is_bored("I feel good today. I will be productive. will kill It") == 2);
    assert (is_bored("You and I are going for a walk") == 0);
}

