using namespace std;

int next_smallest(vector<int> lst){
    // Input Validation
    if (lst.empty()) {
        return -1; // Return -1 instead of None, since C++ doesn't have None
    }

    // Remove Duplicates
    set<int> s(lst.begin(), lst.end());
    vector<int> unique_lst(s.begin(), s.end());

    // Find Smallest Element
    int smallest = *min_element(unique_lst.begin(), unique_lst.end());

    // Find 2nd Smallest Element
    unique_lst.erase(remove(unique_lst.begin(), unique_lst.end(), smallest), unique_lst.end());
    if (unique_lst.empty()) {
        return -1; // Return -1 instead of None
    }
    int second_smallest = *min_element(unique_lst.begin(), unique_lst.end());

    return second_smallest;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (next_smallest({1, 2, 3, 4, 5}) == 2);
    assert (next_smallest({5, 1, 4, 3, 2}) == 2);
    assert (next_smallest({}) == -1);
    assert (next_smallest({1, 1}) == -1);
    assert (next_smallest({1,1,1,1,0}) == 1);
    assert (next_smallest({-35, 34, 12, -45}) == -35);
}

