bool any_int(float a, float b, float c) {
  // Input Validation
  if (a != floor(a) || b != floor(b) || c != floor(c)) {
    return false;
  }

  // Calculate Sums
  float sum_ab = a + b;
  float sum_ac = a + c;
  float sum_bc = b + c;

  // Comparison
  if (sum_ab == c || sum_ac == b || sum_bc == a) {
    return true;
  }

  // Return Result
  return false;
}

