using namespace std;

string encode(string message){
  string output = "";
  for (char c : message) {
    if (isalpha(c)) {
      c = isupper(c) ? tolower(c) : toupper(c); // swap case of the letter
      if (c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u' ||
          c == 'A' || c == 'E' || c == 'I' || c == 'O' || c == 'U') {
        // replace vowel with letter 2 places ahead
        if (c == 'a' || c == 'A') c = c == 'a' ? 'c' : 'C';
        else if (c == 'e' || c == 'E') c = c == 'e' ? 'g' : 'G';
        else if (c == 'i' || c == 'I') c = c == 'i' ? 'k' : 'K';
        else if (c == 'o' || c == 'O') c = c == 'o' ? 'q' : 'Q';
        else if (c == 'u' || c == 'U') c = c == 'u' ? 'w' : 'W';
      }
    }
    output += c;
  }
  return output;
}

