bool isPrime(int num) {
  if (num <= 1) return false;
  for (int i = 2; i * i <= num; i++) {
    if (num % i == 0) return false;
  }
  return true;
}

int skjkasdkd(vector<int> lst) {
  int largestPrime = 0;
  for (int num : lst) {
    if (isPrime(num) && num > largestPrime) {
      largestPrime = num;
    }
  }
  int digitSum = 0;
  while (largestPrime > 0) {
    digitSum += largestPrime % 10;
    largestPrime /= 10;
  }
  return digitSum;
}

