  return c == 'A' || c == 'E' || c == 'I' || c == 'O' || c == 'U';
}

int count_upper(string s){
  int count = 0;
  for (int i = 0; i < s.length(); i++) {
    if (is_even_index(i) && is_uppercase_vowel(s[i])) {
      count++;
    }
  }
  return count;
}

