using namespace std;

bool check_dict_case(map<string,string> dict){
    // Check if the map is empty
    if (dict.empty()) {
        return false;
    }

    // Iterate through each key in the map
    for (auto& pair : dict) {
        // Check if the key is a string (always true since map<string, string>)
        // Check if the key is either in lower case or upper case
        bool allLower = true;
        bool allUpper = true;
        for (char c : pair.first) {
            if (!islower(c)) {
                allLower = false;
            }
            if (!isupper(c)) {
                allUpper = false;
            }
        }
        if (!allLower && !allUpper) {
            return false;
        }
    }

    // If all keys pass the above checks, return true
    return true;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (check_dict_case({{"p","pineapple"}, {"b","banana"}}) == true);
    assert (check_dict_case({{"p","pineapple"}, {"A","banana"}, {"B","banana"}}) == false);
    assert (check_dict_case({{"p","pineapple"}, {"5","banana"}, {"a","apple"}}) == false);
    assert (check_dict_case({{"Name","John"}, {"Age","36"}, {"City","Houston"}}) == false);
    assert (check_dict_case({{"STATE","NC"}, {"ZIP","12345"} }) == true  );
    assert (check_dict_case({{"fruit","Orange"}, {"taste","Sweet"} }) == true  );
    assert (check_dict_case({}) == false);
}

