string rounded_avg(int n, int m) {
    if (n > m) {
        return "-1";
    }

    double avg = (n + m) / 2.0;
    int rounded_avg = floor(avg);

    string binary = "";
    while (rounded_avg > 0) {
        binary = (rounded_avg % 2 == 0 ? "0" : "1") + binary;
        rounded_avg /= 2;
    }

    return binary;
}

