// Helper function to check if an integer has any even digits
bool hasEvenDigit(int num) {
    string str = to_string(num);
    for (char c : str) {
        if ((c - '0') % 2 == 0) {
            return true;
        }
    }
    return false;
}

vector<int> unique_digits(vector<int> x) {
    // Input validation: if input vector is empty, return an empty vector
    if (x.empty()) {
        return {};
    }

    // Filtering step: iterate through each integer and check for even digits
    vector<int> filteredVector;
    for (int num : x) {
        if (!hasEvenDigit(num)) {
            filteredVector.push_back(num);
        }
    }

    // Sorting step: sort the filtered vector in increasing order
    sort(filteredVector.begin(), filteredVector.end());

    // Return the sorted vector
    return filteredVector;
}

