vector<int> f(int n) {
    vector<int> result(n);
    for (int i = 1; i <= n; i++) {
        if (i % 2 == 0) {
            int factorial = 1;
            for (int j = 1; j <= i; j++) {
                factorial *= j;
            }
            result[i - 1] = factorial;
        } else {
            result[i - 1] = i * (i + 1) / 2;
        }
    }
    return result;
}

