string exchange(vector<int> lst1, vector<int> lst2) {
    int evenCountLst1 = 0, oddCountLst1 = 0, evenCountLst2 = 0;

    // Count even and odd elements in lst1
    for (int num : lst1) {
        if (num % 2 == 0) {
            evenCountLst1++;
        } else {
            oddCountLst1++;
        }
    }

    // Count even elements in lst2
    for (int num : lst2) {
        if (num % 2 == 0) {
            evenCountLst2++;
        }
    }

    // Check if exchange is possible
    if (evenCountLst2 >= oddCountLst1) {
        return "YES";
    } else {
        return "NO";
    }
}

