int calculate_digit_sum(int num) {
    int digit_sum = 0;
    int sign = (num < 0) ? -1 : 1;
    num = abs(num);
    while (num > 0) {
        digit_sum += sign * (num % 10);
        num /= 10;
    }
    return digit_sum;
}

int count_nums(vector<int> n) {
    int count = 0;
    for (int element : n) {
        int digit_sum = calculate_digit_sum(element);
        if (digit_sum > 0) {
            count += 1;
        }
    }
    return count;
}

