bool move_one_ball(std::vector<int> arr) {
  if (arr.empty()) return true;

  int min_idx = std::distance(arr.begin(), std::min_element(arr.begin(), arr.end()));

  for (int i = min_idx; i < arr.size() - 1; i++) {
    if (arr[i] > arr[i + 1]) {
      for (int j = 0; j < min_idx; j++) {
        if (arr[j] > arr[(j + 1) % arr.size()]) return false;
      }
      return true;
    }
  }
  return true;
}

