using namespace std;

map<char, int> histogram(string test) {
    map<char, int> letterCount;
    map<char, int> result;

    // Split the input string into individual letters and count the occurrence of each letter
    for (char c : test) {
        if (c != ' ') {
            letterCount[c]++;
        }
    }

    // Find the maximum occurrence
    int maxCount = 0;
    for (auto& pair : letterCount) {
        maxCount = max(maxCount, pair.second);
    }

    // Identify letters with the maximum occurrence and create the output map
    for (auto& pair : letterCount) {
        if (pair.second == maxCount) {
            result[pair.first] = pair.second;
        }
    }

    return result;
}


