vector<string> reverse_delete(string s, string c) {
    // Input Validation
    if (s.empty() || c.empty()) {
        return {"", "False"};
    }

    // Character Deletion
    string result = "";
    for (char ch : s) {
        if (c.find(ch) == string::npos) {
            result += ch;
        }
    }

    // Palindrome Check
    string reversed = result;
    reverse(reversed.begin(), reversed.end());
    bool is_palindrome = result == reversed;

    // Result Formatting
    vector<string> res;
    res.push_back(result);
    res.push_back(is_palindrome ? "True" : "False");
    return res;
}

