int count_ones_in_row(vector<int> row) {
  int count = 0;
  for (int element : row) {
    if (element == 1) {
      count++;
    }
  }
  return count;
}

int max_fill(vector<vector<int>> grid, int capacity) {
  int total_buckets = 0;
  for (vector<int> row : grid) {
    int water_in_well = count_ones_in_row(row);
    int buckets_needed = ceil((double)water_in_well / capacity);
    total_buckets += buckets_needed;
  }
  return total_buckets;
}

