// Function to count the number of ones in the binary representation of an integer
int countOnes(int n) {
    int count = 0;
    while (n) {
        count += n & 1;
        n >>= 1;
    }
    return count;
}

// Custom comparison function for sorting
bool compare(int a, int b) {
    int onesA = countOnes(abs(a));
    int onesB = countOnes(abs(b));
    if (onesA == onesB) {
        return a < b;
    }
    return onesA < onesB;
}

vector<int> sort_array(vector<int> arr) {
    // Check for empty input vector
    if (arr.empty()) {
        return arr;
    }

    // Sort the vector using the custom comparison function
    sort(arr.begin(), arr.end(), compare);
    return arr;
}

