using namespace std;

string match_parens(vector<string> lst){
  int open_count1 = 0, close_count1 = 0, open_count2 = 0, close_count2 = 0;

  // Count Open and Close Parentheses
  for (char c : lst[0]) {
    if (c == '(') open_count1++;
    else close_count1++;
  }
  for (char c : lst[1]) {
    if (c == '(') open_count2++;
    else close_count2++;
  }

  // Determine Balance
  if (open_count1 + open_count2 != close_count1 + close_count2) {
    return "No";
  }

  // Check Concatenation Order
  if (open_count1 + close_count2 == open_count2 + close_count1) {
    return "Yes";
  } else {
    return "No";
  }
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (match_parens({"()(", ")"}) == "Yes");
    assert (match_parens({")", ")"}) == "No");
    assert (match_parens({"(()(())", "())())"}) == "No");
    assert (match_parens({")())", "(()()("}) == "Yes");
    assert (match_parens({"(())))", "(()())(("}) == "Yes");
    assert (match_parens({"()", "())"}) == "No");
    assert (match_parens({"(()(", "()))()"}) == "Yes");
    assert (match_parens({"((((", "((())"}) == "No");
    assert (match_parens({")(()", "(()("}) == "No");
    assert (match_parens({")(", ")("}) == "No");
    assert (match_parens({"(", ")"}) == "Yes");
    assert (match_parens({")", "("}) == "Yes" );
}

