bool valid_date(string date) {
    // Input Validation: Check if the input date string is not empty
    if (date.empty()) {
        return false;
    }

    // Date String Format Validation: Check if the input date string matches the format mm-dd-yyyy
    if (date.length() != 10 || date[2] != '-' || date[5] != '-') {
        return false;
    }

    // Month Extraction and Validation
    int month = stoi(date.substr(0, 2));
    if (month < 1 || month > 12) {
        return false;
    }

    // Day Extraction and Validation
    int day = stoi(date.substr(3, 2));
    int maxDays;
    if (month == 2) {
        maxDays = 29;
    } else if (month == 4 || month == 6 || month == 9 || month == 11) {
        maxDays = 30;
    } else {
        maxDays = 31;
    }
    if (day < 1 || day > maxDays) {
        return false;
    }

    // Year Extraction (not validated in this implementation)
    int year = stoi(date.substr(6, 4));

    // Return Result: If all validations pass, return true
    return true;
}

