vector<string> split_words(string txt) {
    if (txt.empty()) {
        return {};
    }

    if (txt.find(' ') != string::npos) {
        vector<string> result;
        size_t pos = 0;
        while ((pos = txt.find(' ')) != string::npos) {
            result.push_back(txt.substr(0, pos));
            txt.erase(0, pos + 1);
        }
        result.push_back(txt);
        return result;
    }

    if (txt.find(',') != string::npos) {
        vector<string> result;
        size_t pos = 0;
        while ((pos = txt.find(',')) != string::npos) {
            result.push_back(txt.substr(0, pos));
            txt.erase(0, pos + 1);
        }
        result.push_back(txt);
        return result;
    }

    int count = 0;
    for (char c : txt) {
        if (c >= 'a' && c <= 'z' && (c - 'a') % 2 != 0) {
            count++;
        }
    }
    return {to_string(count)};
}

