// Helper function to check if a number is prime
bool isPrime(int num) {
    if (num <= 1) return false;
    for (int i = 2; i <= sqrt(num); i++) {
        if (num % i == 0) return false;
    }
    return true;
}

string intersection(vector<int> interval1, vector<int> interval2) {
    // Validate input intervals
    if (interval1.size() != 2 || interval2.size() != 2) return "NO";
    if (interval1[0] > interval1[1] || interval2[0] > interval2[1]) return "NO";

    // Find interval intersection
    int intersectionStart = max(interval1[0], interval2[0]);
    int intersectionEnd = min(interval1[1], interval2[1]);
    if (intersectionStart > intersectionEnd) return "NO";

    // Calculate intersection length
    int intersectionLength = intersectionEnd - intersectionStart + 1;

    // Primality check
    if (isPrime(intersectionLength)) return "YES";
    else return "NO";
}

