bool is_sorted(vector<int> lst) {
  // Input validation
  if (lst.empty()) {
    return false;
  }

  // Count occurrences
  unordered_map<int, int> counts;
  for (int num : lst) {
    counts[num]++;
  }

  // Check for duplicates
  for (auto& pair : counts) {
    if (pair.second > 2) {
      return false;
    }
  }

  // Check for sorting
  for (int i = 0; i < lst.size() - 1; i++) {
    if (lst[i] > lst[i + 1]) {
      return false;
    }
  }

  // Return result
  return true;
}


