vector<int> tri(int n) {
    vector<int> trib(n + 2, 0);
    trib[1] = 3;
    trib[2] = 2;
    for (int i = 3; i <= n + 1; i++) {
        if (i % 2 == 0) {
            trib[i] = 1 + i / 2;
        } else {
            trib[i] = trib[i - 1] + trib[i - 2] + trib[i + 1];
        }
    }
    // Handle the case where n is 0 or 1
    if (n == 0) {
        return vector<int>(1, 1);
    } else if (n == 1) {
        return vector<int>({1, 3});
    }
    // Return the constructed trib vector
    return vector<int>(trib.begin(), trib.begin() + n + 1);
}

