using namespace std;

struct Path {
    vector<int> values;
    int x, y;
    bool operator<(const Path& other) const {
        for (int i = 0; i < min(values.size(), other.values.size()); i++) {
            if (values[i] < other.values[i]) return true;
            if (values[i] > other.values[i]) return false;
        }
        return values.size() < other.values.size();
    }
};

vector<int> minPath(vector<vector<int>> grid, int k) {
    int n = grid.size();
    priority_queue<Path> pq;
    set<pair<int, int>> visited;

    // Find the cell with the minimum value in the grid
    int minX = 0, minY = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (grid[i][j] < grid[minX][minY]) {
                minX = i;
                minY = j;
            }
        }
    }

    // Construct an initial path of length k starting from the minimum value cell
    Path initialPath;
    initialPath.values.push_back(grid[minX][minY]);
    initialPath.x = minX;
    initialPath.y = minY;
    pq.push(initialPath);

    while (!pq.empty()) {
        Path currentPath = pq.top();
        pq.pop();

        if (currentPath.values.size() == k) {
            return currentPath.values;
        }

        // Explore neighbor cells
        int dx[] = {-1, 1, 0, 0};
        int dy[] = {0, 0, -1, 1};
        for (int i = 0; i < 4; i++) {
            int newX = currentPath.x + dx[i];
            int newY = currentPath.y + dy[i];

            // Check if the neighbor cell is within the grid boundaries
            if (newX >= 0 && newX < n && newY >= 0 && newY < n) {
                // Check if the neighbor cell has not been visited before
                if (visited.find({newX, newY}) == visited.end()) {
                    Path newPath = currentPath;
                    newPath.values.push_back(grid[newX][newY]);
                    newPath.x = newX;
                    newPath.y = newY;
                    pq.push(newPath);
                    visited.insert({newX, newY});
                }
            }
        }
    }

    return {};
}

