int can_arrange(vector<int> arr) {
    if (arr.empty()) {
        return -1;
    }

    int max_index = -1;
    for (int i = 1; i < arr.size(); i++) {
        if (arr[i] < arr[i - 1]) {
            max_index = i;
        }
    }

    return max_index;
}

