bool is_nested(string str) {
    // Validate input string
    for (char c : str) {
        if (c != '[' && c != ']') {
            throw invalid_argument("Input string must only contain square brackets");
        }
    }

    // Initialize variables
    int nesting_level = 0;
    bool is_nested = false;

    // Iterate through the string
    for (char c : str) {
        if (c == '[') {
            nesting_level++;
            if (nesting_level > 1) {
                is_nested = true;
            }
        } else if (c == ']') {
            nesting_level--;
            if (nesting_level < 0) {
                return false; // Invalid bracket sequence
            }
        }
    }

    // Check validity of bracket sequence
    if (nesting_level != 0) {
        return false;
    }

    return is_nested;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (is_nested("[[]]") == true);
    assert (is_nested("[]]]]]]][[[[[]") == false);
    assert (is_nested("[][]") == false);
    assert (is_nested(("[]")) == false);
    assert (is_nested("[[[[]]]]") == true);
    assert (is_nested("[]]]]]]]]]]") == false);
    assert (is_nested("[][][[]]") == true);
    assert (is_nested("[[]") == false);
    assert (is_nested("[]]") == false);
    assert (is_nested("[[]][[") == true);
    assert (is_nested("[[][]]") == true);
    assert (is_nested("") == false);
    assert (is_nested("[[[[[[[[") == false);
    assert (is_nested("]]]]]]]]") == false);
}

