vector<int> largest_smallest_integers(vector<int> lst) {
  if (lst.empty()) {
    return {0, 0};
  }

  int largest_negative = 0;
  int smallest_positive = 0;

  for (int num : lst) {
    if (num < 0) {
      largest_negative = (largest_negative == 0) ? num : max(largest_negative, num);
    } else if (num > 0) {
      smallest_positive = (smallest_positive == 0) ? num : min(smallest_positive, num);
    }
  }

  return {largest_negative, smallest_positive};
}

