long long factorial(int k) {
    long long result = 1;
    for (int i = 2; i <= k; i++) {
        result *= i;
    }
    return result;
}

long long product(long long* factorials, int size) {
    long long result = 1;
    for (int i = 0; i < size; i++) {
        result *= factorials[i];
    }
    return result;
}

long long special_factorial(int n) {
    long long* factorials = new long long[n];
    for (int i = 0; i < n; i++) {
        factorials[i] = factorial(n - i);
    }
    long long result = product(factorials, n);
    delete[] factorials;
    return result;
}


