// Helper function to check if a boost::any object is a valid number
bool isValidNumber(const boost::any& a) {
  return a.type() == typeid(int) || a.type() == typeid(float) || a.type() == typeid(std::string);
}

// Helper function to format a string with a comma as a decimal separator
boost::any formatString(const boost::any& a) {
  std::string str = boost::any_cast<std::string>(a);
  std::replace(str.begin(), str.end(), ',', '.');
  return str;
}

// Helper function to convert a string to a number
boost::any convertStringToNumber(const boost::any& a) {
  std::string str = boost::any_cast<std::string>(a);
  try {
    return std::stof(str);
  } catch (const std::invalid_argument& e) {
    throw std::invalid_argument("Invalid string input");
  }
}

// Helper function to compare two boost::any objects
bool compare(const boost::any& a, const boost::any& b) {
  if (a.type() == typeid(int) && b.type() == typeid(int)) {
    return boost::any_cast<int>(a) > boost::any_cast<int>(b);
  } else if (a.type() == typeid(float) && b.type() == typeid(float)) {
    return boost::any_cast<float>(a) > boost::any_cast<float>(b);
  } else if (a.type() == typeid(std::string) && b.type() == typeid(std::string)) {
    return boost::any_cast<std::string>(a) > boost::any_cast<std::string>(b);
  } else {
    throw std::invalid_argument("Invalid input types");
  }
}

boost::any compare_one(boost::any a, boost::any b) {
  // Input Validation
  if (!isValidNumber(a) || !isValidNumber(b)) {
    throw std::invalid_argument("Invalid input");
  }

  // String Formatting
  if (a.type() == typeid(std::string)) {
    a = formatString(a);
  }
  if (b.type() == typeid(std::string)) {
    b = formatString(b);
  }

  // Type Conversion
  if (a.type() == typeid(std::string)) {
    a = convertStringToNumber(a);
  }
  if (b.type() == typeid(std::string)) {
    b = convertStringToNumber(b);
  }

  // Comparison
  if (compare(a, b)) {
    return a;
  } else if (compare(b, a)) {
    return b;
  } else {
    return "None";
  }
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (boost::any_cast<int>(compare_one(1, 2)) == 2);
    assert (boost::any_cast<double>(compare_one(1, 2.5))== 2.5);
    assert (boost::any_cast<int>(compare_one(2, 3)) == 3);
    assert (boost::any_cast<int>(compare_one(5, 6)) == 6);
    assert (boost::any_cast<string>(compare_one(1, string("2,3")))== "2,3");
    assert (boost::any_cast<string>(compare_one(string("5,1"), string("6"))) == "6");
    assert (boost::any_cast<string>(compare_one(string("1"), string("2"))) == "2");
    assert (boost::any_cast<string>(compare_one(string("1"), 1)) == "None");
}

