bool is_equal_to_sum_even(int n) {
    if (n <= 0) return false; // Validate input

    bool found = false;
    for (int a = 2; a <= n / 4; a += 2) {
        for (int b = a; b <= n / 3; b += 2) {
            for (int c = b; c <= n / 2; c += 2) {
                int d = n - a - b - c;
                if (d >= c && d % 2 == 0) {
                    if (found) return false; // More than one combination found
                    found = true;
                }
            }
        }
    }
    return found;
}


