// Function to count the number of digits in the file name
int count_digits(string file_name) {
  int count = 0;
  for (char c : file_name) {
    if (c >= '0' && c <= '9') {
      count++;
    }
  }
  return count;
}

// Function to verify that the file name contains exactly one dot
bool has_exactly_one_dot(string file_name) {
  int count = 0;
  for (char c : file_name) {
    if (c == '.') {
      count++;
    }
  }
  return count == 1;
}

// Function to split the file name into two substrings: one before the dot and one after the dot
void split_file_name(string file_name, string& before_dot, string& after_dot) {
  size_t dot_pos = file_name.find('.');
  before_dot = file_name.substr(0, dot_pos);
  after_dot = file_name.substr(dot_pos + 1);
}

// Function to check that the substring before the dot is not empty and starts with a letter from the Latin alphabet
bool is_valid_before_dot(string before_dot) {
  return !before_dot.empty() && ((before_dot[0] >= 'a' && before_dot[0] <= 'z') || (before_dot[0] >= 'A' && before_dot[0] <= 'Z'));
}

// Function to check that the substring after the dot is one of the allowed extensions
bool is_valid_after_dot(string after_dot) {
  return after_dot == "txt" || after_dot == "exe" || after_dot == "dll";
}

// Main function to check the file name
string file_name_check(string file_name) {
  if (count_digits(file_name) > 3) return "No";
  if (!has_exactly_one_dot(file_name)) return "No";
  string before_dot, after_dot;
  split_file_name(file_name, before_dot, after_dot);
  if (!is_valid_before_dot(before_dot)) return "No";
  if (!is_valid_after_dot(after_dot)) return "No";
  return "Yes";
}

