string fix_spaces(string text) {
    string result = "";
    int space_count = 0;

    for (char c : text) {
        if (c == ' ') {
            space_count++;
            if (space_count == 1 || space_count == 2) {
                result += '_';
            } else if (space_count > 2) {
                result += '-';
                space_count = 0;
            }
        } else {
            if (space_count > 2) {
                result += '-';
            }
            space_count = 0;
            result += c;
        }
    }

    return result;
}

