// Function to calculate the greatest common divisor (GCD) using the Euclidean algorithm
int gcd(int a, int b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

// Function to parse a fraction string into numerator and denominator
void parseFraction(const string& fraction, int& numerator, int& denominator) {
    stringstream ss(fraction);
    string token;
    getline(ss, token, '/');
    numerator = stoi(token);
    getline(ss, token);
    denominator = stoi(token);
}

bool simplify(string x, string n) {
    int xNumerator, xDenominator, nNumerator, nDenominator;
    parseFraction(x, xNumerator, xDenominator);
    parseFraction(n, nNumerator, nDenominator);

    // Multiply the fractions
    int productNumerator = xNumerator * nNumerator;
    int productDenominator = xDenominator * nDenominator;

    // Simplify the fraction by dividing both numerator and denominator by the GCD
    int gcdValue = gcd(productNumerator, productDenominator);
    productNumerator /= gcdValue;
    productDenominator /= gcdValue;

    // Check if the simplified denominator is 1
    return productDenominator == 1;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (simplify("1/5", "5/1") == true);
    assert (simplify("1/6", "2/1") == false);
    assert (simplify("5/1", "3/1") == true);
    assert (simplify("7/10", "10/2") == false);
    assert (simplify("2/10", "50/10") == true);
    assert (simplify("7/2", "4/2") == true);
    assert (simplify("11/6", "6/1") == true);
    assert (simplify("2/3", "5/2") == false);
    assert (simplify("5/2", "3/5") == false);
    assert (simplify("2/4", "8/4") == true);
    assert (simplify("2/4", "4/2") == true);
    assert (simplify("1/5", "5/1") == true);
    assert (simplify("1/5", "1/5") == false);
}

