// Function to calculate the sum of digits for a single integer
int calculate_sum_of_digits(int num) {
    int sum = 0;
    int sign = (num < 0) ? -1 : 1;
    num = abs(num);
    while (num > 0) {
        sum += sign * (num % 10);
        num /= 10;
    }
    return sum;
}

// Custom comparison function for sorting
bool compare_pairs(const std::pair<int, int>& a, const std::pair<int, int>& b) {
    if (a.first == b.first) {
        return a.second < b.second;
    }
    return a.first < b.first;
}

vector<int> order_by_points(vector<int> nums) {
    // Create a vector of pairs to store integers and their sum of digits
    std::vector<std::pair<int, int>> num_pairs;
    for (int i = 0; i < nums.size(); i++) {
        int sum = calculate_sum_of_digits(nums[i]);
        num_pairs.push_back(std::make_pair(sum, i));
    }

    // Sort the vector of pairs based on the sum of digits and original index
    std::sort(num_pairs.begin(), num_pairs.end(), compare_pairs);

    // Extract and return the sorted integers
    vector<int> sorted_nums;
    for (int i = 0; i < num_pairs.size(); i++) {
        sorted_nums.push_back(nums[num_pairs[i].second]);
    }
    return sorted_nums;
}

