vector<string> bf(string planet1, string planet2) {
    vector<string> planets = {"Mercury", "Venus", "Earth", "Mars", "Jupiter", "Saturn", "Uranus", "Neptune"};

    // Validate Input Planets
    if (find(planets.begin(), planets.end(), planet1) == planets.end() || 
        find(planets.begin(), planets.end(), planet2) == planets.end()) {
        return {};
    }

    // Determine Planet Order
    int index1 = find(planets.begin(), planets.end(), planet1) - planets.begin();
    int index2 = find(planets.begin(), planets.end(), planet2) - planets.begin();

    // Swap indices if necessary
    if (index1 > index2) {
        swap(index1, index2);
    }

    // Generate Intermediate Planets
    vector<string> intermediatePlanets(planets.begin() + index1 + 1, planets.begin() + index2);

    return intermediatePlanets;
}

