// Function to check if a number is prime
bool is_prime(int n) {
  if (n <= 1) return false;
  for (int i = 2; i * i <= n; i++) {
    if (n % i == 0) return false;
  }
  return true;
}

// Function to return x if n is prime, y otherwise
int x_or_y(int n, int x, int y) {
  if (is_prime(n)) {
    return x;
  } else {
    return y;
  }
}


