vector<int> compare(vector<int> game, vector<int> guess) {
  // Check if input vectors have the same length
  if (game.size() != guess.size()) {
    throw invalid_argument("Input vectors must have the same length");
  }

  // Initialize output vector
  vector<int> result(game.size());

  // Iterate over input vectors
  for (int i = 0; i < game.size(); i++) {
    // Compare elements and calculate absolute difference
    int diff = abs(game[i] - guess[i]);

    // Store result in output vector
    result[i] = diff;
  }

  // Return output vector
  return result;
}

