// Helper function to generate all rotations of a string
vector<string> generateRotations(string b) {
  vector<string> rotations;
  int n = b.length();
  for (int i = 0; i < n; i++) {
    string rotation = b.substr(i, n - i) + b.substr(0, i);
    rotations.push_back(rotation);
  }
  return rotations;
}

// Helper function to check if a string is a substring of another string
bool isSubstring(string a, string b) {
  return a.find(b) != string::npos;
}

// Main function to check if the second word or any of its rotations is a substring in the first word
bool cycpattern_check(string a, string b) {
  // Input validation
  if (a.empty() || b.empty()) {
    return false;
  }

  // Generate rotations of b
  vector<string> rotations = generateRotations(b);

  // Iterate and check
  for (const string& rotation : rotations) {
    if (isSubstring(a, rotation)) {
      return true;
    }
  }

  // Return result
  return false;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert  (cycpattern_check("xyzw","xyw") == false );
    assert  (cycpattern_check("yello","ell") == true );
    assert  (cycpattern_check("whattup","ptut") == false );
    assert  (cycpattern_check("efef","fee") == true );
    assert  (cycpattern_check("abab","aabb") == false );
    assert  (cycpattern_check("winemtt","tinem") == true );
}

