bool right_angle_triangle(float a, float b, float c) {
    // Validate input
    if (a <= 0 || b <= 0 || c <= 0) {
        return false;
    }

    // Sort side lengths
    float sides[] = {a, b, c};
    std::sort(sides, sides + 3);
    a = sides[0];
    b = sides[1];
    c = sides[2];

    // Check Pythagorean Theorem
    return (a * a + b * b) == (c * c);
}

