using namespace std;

string find_max(vector<string> words) {
    string max_string;
    int max_unique_chars = 0;

    for (const auto& word : words) {
        unordered_set<char> unique_chars(word.begin(), word.end());
        int unique_chars_count = unique_chars.size();

        if (unique_chars_count > max_unique_chars) {
            max_unique_chars = unique_chars_count;
            max_string = word;
        } else if (unique_chars_count == max_unique_chars) {
            if (word < max_string) {
                max_string = word;
            }
        }
    }

    return max_string;
}


