vector<int> generate_integers(int a, int b) {
    // Validate Input
    if (a <= 0 || b <= 0) {
        return {};
    }

    // Determine Range
    int min_val = std::min(a, b);
    int max_val = std::max(a, b);

    // Generate Even Digits
    vector<int> even_digits;
    for (int i = min_val; i <= max_val; ++i) {
        if (i < 10 && i % 2 == 0) {
            even_digits.push_back(i);
        }
    }

    // Sort Even Digits
    std::sort(even_digits.begin(), even_digits.end());

    // Return Result
    return even_digits;
}

#undef NDEBUG
#include<assert.h>
bool issame(vector<int> a,vector<int>b){
    if (a.size()!=b.size()) return false;
    for (int i=0;i<a.size();i++)
    {
    if (a[i]!=b[i]) return false;
    }
    return true;
}
int main(){
    assert (issame(generate_integers(2, 10) , {2, 4, 6, 8}));
    assert (issame(generate_integers(10, 2) , {2, 4, 6, 8}));
    assert (issame(generate_integers(132, 2) , {2, 4, 6, 8}));
    assert (issame(generate_integers(17,89) , {}));
}

