// Function to validate input vectors
bool validate_input(vector<string> operato, vector<int> operand) {
  if (operato.size() != operand.size() - 1) {
    return false;
  }
  for (int i = 0; i < operand.size(); i++) {
    if (operand[i] < 0) {
      return false;
    }
  }
  if (operato.size() < 1 || operand.size() < 2) {
    return false;
  }
  return true;
}

// Function to build algebraic expression
string build_expression(vector<string> operato, vector<int> operand) {
  string expression = "";
  expression += to_string(operand[0]);
  for (int i = 0; i < operato.size(); i++) {
    expression += " " + operato[i] + " ";
    expression += to_string(operand[i + 1]);
  }
  return expression;
}

// Function to evaluate algebraic expression
int evaluate_expression(string expression) {
  FILE *fp = popen("bc", "w");
  if (fp == NULL) {
    return -1;
  }
  fprintf(fp, "%s\n", expression.c_str());
  int result;
  fscanf(fp, "%d", &result);
  pclose(fp);
  return result;
}

// Main function to perform algebraic operation
int do_algebra(vector<string> operato, vector<int> operand) {
  // Input Validation
  if (!validate_input(operato, operand)) {
    return -1;
  }

  // Build Algebraic Expression
  string expression = build_expression(operato, operand);

  // Evaluate Algebraic Expression
  int result = evaluate_expression(expression);

  return result;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (do_algebra({"**", "*", "+"}, {2, 3, 4, 5}) == 37);
    assert (do_algebra({"+", "*", "-"}, {2, 3, 4, 5}) == 9);
    assert (do_algebra({"//", "*"}, {7, 3, 4}) == 8);
}

