/*
Given a positive floating point number, it can be decomposed into
and integer part (largest integer smaller than given number) and decimals
(leftover part always smaller than 1).

Return the decimal part of the number.
>>> truncate_number(3.5)
0.5
*/
#include<stdio.h>
#include<math.h>
using namespace std;
float truncate_number(float number){
    if (number <= 0) {
        // Handle invalid input (non-positive number)
        printf("Error: Input must be a positive floating-point number.\n");
        return -1.0f; // Return an error value
    }
    float integer_part = floor(number);
    float decimal_part = number - integer_part;
    return decimal_part;
}

#undef NDEBUG
#include<assert.h>
int main(){
 assert (truncate_number(3.5) == 0.5); 
 assert (abs(truncate_number(1.33) - 0.33) < 1e-4);
  assert (abs(truncate_number(123.456) - 0.456) < 1e-4);
}

#include <stdlib.h>
