bool below_zero(vector<int> operations) {
    int balance = 0; // Start with a zero balance

    for (int operation : operations) {
        balance += operation; // Update the balance based on the current operation

        if (balance < 0) {
            return true; // If balance falls below zero, return true
        }
    }

    return false; // If no point where balance is negative, return false
}

// Example usage and check function

#undef NDEBUG
#include<assert.h>
int main(){
    assert (below_zero({}) == false);
    assert (below_zero({1, 2, -3, 1, 2, -3}) == false);
    assert (below_zero({1, 2, -4, 5, 6}) == true);
    assert (below_zero({1, -1, 2, -2, 5, -5, 4, -4}) == false);
    assert (below_zero({1, -1, 2, -2, 5, -5, 4, -5}) == true);
    assert (below_zero({1, -2, 2, -2, 5, -5, 4, -4}) == true);
}

