string string_xor(string a, string b) {
    // Ensure that both strings are of equal length for simplicity.
    if (a.length() != b.length()) {
        cout << "Error: Strings must be of the same length." << endl;
        return "";
    }

    string result = "";  // Initialize an empty string to store the result.

    // Iterate through each character in the strings and perform XOR operation.
    for (int i = 0; i < a.length(); ++i) {
        if (a[i] == b[i]) {
            result += '0';  // If both characters are the same, append '0' to the result string.
        } else {
            result += '1';  // If characters are different, append '1'.
        }
    }

    return result;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (string_xor("111000", "101010") == "010010");
     assert (string_xor("1", "1") == "0");
    assert (string_xor("0101", "0000") == "0101");

}

