bool has_close_elements(std::vector<float> numbers, float threshold) {
    // Sort the vector to make it easier to find close elements.
    std::sort(numbers.begin(), numbers.end());

    // Iterate through the sorted list and check if any two consecutive elements
    // are closer than the given threshold.
    for (size_t i = 0; i < numbers.size() - 1; ++i) {
        if (std::abs(numbers[i] - numbers[i + 1]) < threshold) {
            return true;
        }
    }

    // If no close elements were found, return false.
    return false;
}

// Example usage and check function

