vector<int> rolling_max(vector<int> numbers) {
    if (numbers.empty()) return {};  // Return an empty vector if input is empty

    vector<int> result;
    int max_so_far = numbers[0];  // Initialize the maximum value with the first element
    for (int num : numbers) {
        if (num > max_so_far) {
            max_so_far = num;  // Update the maximum value if current number is greater
        }
        result.push_back(max_so_far);  // Add the current maximum to the result vector
    }

    return result;
}

